mapper.select:
SELECT user_id
FROM User
WHERE sns_id = #{snsId}

SELECT count(*)
FROM User
WHERE sns_id = #{snsId}

mapper.update:
UPDATE User
SET    name = #{name},
       nickname = #{nickname},
       email = #{email}
WHERE sns_id = #{snsId}

UPDATE User
SET    profile_picture = #{file}
WHERE sns_id = #{snsId}
